// Capturamos los selectores
const menuBtn = document.querySelector('.menu-btn');
const hamburger = document.querySelector('.menu-btn__burger');
const nav = document.querySelector('.nav');
const menuNav = document.querySelector('.menu-nav');
const navItems = document.querySelectorAll('.menu-nav__item');
// Vista de menu inicialmente en false
let showMenu = false;

// Realizamos el evento que al dar click hace la rotacion de css en el menu hamburguesa
menuBtn.addEventListener('click' , toogleMenu);


// Funcion que al dar click rota la burgen por una x
function toogleMenu () {
    if (!showMenu) {
        hamburger.classList.add('open');
        nav.classList.add('open');
        menuNav.classList.add('open');
        navItems.forEach(item => item.classList.add('open'));

        showMenu = true;
    } else {
        hamburger.classList.remove('open');
        nav.classList.remove('open');
        menuNav.classList.remove('open');
        navItems.forEach(item => item.classList.remove('open'));

        showMenu = false;
    }
}

